                    [ 14, 13, 6, 'Formulaire de réservation pour courses de taxis', 'D&eacute;veloppement d\'une formulaire de r&eacute;servation<br />comprenant :<br /><ul><li>Une partie administration avec gestion des tarifs et des destinations et gestion des r&eacute;servations.</li><li>Une partie &quot;front&quot; sur laquelle le client peut faire une r&eacute;servation de course.<br /></li></ul>', 300, '2008-11-20', '2008-11-24', '2008-11-24', '2008-11-24', 1, 1 ],
                    [ 15, 16, 6, 'Carte d\'Europe', 'Modifier une application Flash statique &quot;carte d\'Europe&quot; en vue de :<br /><ul><li>Rendre le flash dynamique.</li><li>Cr&eacute;er un script de g&eacute;n&eacute;ration de fichier XML pour le param&eacute;trage du flash.<br /></li></ul>', 190, '2008-11-23', '2008-11-27', '2008-12-02', '2008-12-02', 1, 1 ],
                    [ 16, 13, 6, 'Création d\'un back-office pour www.enneigement.com', 'Le back office doit contenir :<br /><ul><li>Gestion des utilisateurs permettant &agrave; un utilisateur de pouvoir se connecter uniquement &agrave; une station.</li><li>Gestion en modification des diff&eacute;rentes stations d&eacute;j&agrave; inscrites avec une mise en avant des champs les plus souvent modifi&eacute;s.<br /></li></ul>', 400, '2008-11-24', '2008-11-28', '2008-12-01', '2008-12-01', 1, 1 ],
                    [ 17, 13, 6, 'Modification du composant Joomla! « neorecruit 1.4.4 »', 'Cette modification comprend :<br /><ul><li>Modification de l\'administration afin de faire apparaitre le code postal et la ville des utilisateurs enregistr&eacute;s pour une offre d\'emploi.</li><li>Possibilit&eacute; de trier les r&eacute;sultats suivant le code postal et la ville.<br /></li></ul>', 150, '2008-12-04', '2008-12-08', '2008-12-08', '2008-12-08', 1, 1 ],
                    [ 18, 15, 6, '- 5437 - Site internet de jeux concours (www.gagnergagner.com) -', '<strong>R&eacute;alisation compl&egrave;te du site gagnergagner.com</strong><br />Cette prestation comprend :<br /><ul><li>R&eacute;alisation d&rsquo;une charte graphique.</li><li>D&eacute;coupage des pages et int&eacute;gration.</li><li>Codage du site.</li><li>Un back office permettant de g&eacute;rer le site.<br /></li></ul><br />', 950, '2008-12-08', '2008-12-19', '2009-03-17', '2009-03-17', 1, 1 ],
