                    [ 254, 41, 6, 'Hébergement du site ecolonett.fr', '<p>Cette prestation comprend :</p>\r\n<ul>\r\n<li>Le renouvellement du nom de domaine.</li>\r\n<li>L\'h&eacute;bergement du site pour une dur&eacute;e d\'un an &agrave; partir du 08/04/2013.</li>\r\n</ul>', 60, '2013-03-18', '2013-03-18', '2013-03-18', '2013-03-26', 1, 1 ],
                    [ 255, 39, 6, 'Modification RESTOMALIN (mars 2013)', '<p>Cette prestation comprend :</p>\r\n<ul>\r\n<li>La mise en place d\'un acc&egrave;s "partenaire" au Back-Office. <strong>90&euro;</strong></li>\r\n<li>Modification du BO pour permettre l\'upload de 2 images destin&eacute;es aux sites vitrines. Les dimensions sont conserv&eacute;es. <strong>90&euro;</strong></li>\r\n</ul>', 180, '2013-03-21', '2013-03-31', '2013-05-04', '2013-07-08', 1, 1 ],
                    [ 256, 13, 6, 'Création d\'un planning de réservation', '<p>Cette prestation comprend :</p>\r\n<ul>\r\n<li>Gestion des tarifs de location par semaine.</li>\r\n<li>Gestion des &eacute;tats de r&eacute;servation.</li>\r\n<li>R&eacute;servation en ligne depuis le Front Office et le Back Office.</li>\r\n<li>Affichage 2 x 3 mois du planning sur le Front Office.</li>\r\n</ul>', 790, '2013-03-22', '2013-03-31', '2013-05-02', '2013-05-14', 1, 1 ],
                    [ 257, 31, 6, 'Modification CARGO [ mars à mai 2013)', '<p>Cette prestation comprend :</p>\r\n<ul>\r\n<li>Ajout de 2 types de v&eacute;hicules au site principal. <strong>110</strong></li>\r\n<li>Param&eacute;trage de leurs tarifs de base.</li>\r\n<li>Modifications textuelles sur la pr&eacute;sentation de la liste des agences. <strong>80</strong></li>\r\n<li>Suppression de toutes les informations bancaires des r&eacute;servations pass&eacute;es. <strong>40</strong></li>\r\n<li>Modification de l\'enregistrement des r&eacute;servations afin de ne plus stocker les informations bancaires. <strong>30</strong></li>\r\n<li>Ajout d\'exception dans le syst&egrave;me de r&eacute;servation (Point Cargo Corse). <strong>40</strong></li>\r\n<li>Cr&eacute;ation du module d\'export de la base client. Ce module est accessible pour toutes les agences, depuis le Back-Office. <strong>100</strong></li>\r\n<li>Modification du site pour permettre l\'affichage de l\'agence de Belgique. <strong>100</strong></li>\r\n<li>Ajout des tarifications exceptionnelles pour les points Cargo. <strong>90</strong></li>\r\n</ul>', 590, '2013-03-25', '2013-03-31', '2013-06-18', '2013-07-03', 1, 1 ],
