                    [ 364, 52, 8, 'Statistiques AAB', '<p>Reprise de la page d\'accueil des statistiques AAB</p>', 200, '2015-01-27', NULL, NULL, NULL, 0, 0 ],
                    [ 365, 13, 6, 'Gestion de magasins - Evolution', '<p>Permettre &agrave; l\'admin d\'ajouter des commerciaux aux magasins.</p>\r\n<p>Pouvoir choisir une p&eacute;riode pour l\'affichage des statistiques. Solution avec calendriers sur lesquels seuls les lundis sont s&eacute;lectionnables.</p>', 250, '2015-02-12', '2015-02-16', '2015-03-07', '2015-03-18', 1, 0 ],
                    [ 366, 41, 6, 'Hébergement du site ecolonett.fr', '<p>Cette prestation comprend :</p>\r\n<ul>\r\n<li>Le renouvellement du nom de domaine.</li>\r\n<li>L\'h&eacute;bergement du site pour une dur&eacute;e d\'un an &agrave; partir du 08/04/2015.</li>\r\n</ul>', 60, '2015-02-12', '2015-02-12', '2015-02-12', '2015-02-25', 1, 0 ],
                    [ 367, 51, 6, 'Renouvellement de nom de domaine', '<p>Renouvellement du nom de domaine ainsi que de l\'h&eacute;bergement de "deluxcars.fr" pour 1 an.</p>', 60, '2015-03-07', '2015-03-07', '2015-03-07', '2015-03-26', 1, 0 ],
                    [ 368, 13, 6, 'Inscriptions de bénévoles', '<p>Cette prestation comprend :</p>\r\n<ul>\r\n<li>Ajout d\'un formulaire d\'inscription de b&eacute;n&eacute;voles avec listes d&eacute;roulantes et champs obligatoires.</li>\r\n<li>Gestion des inscriptions via le back office existant.</li>\r\n<li>Edition, modification et suppression des inscriptions.</li>\r\n<li>Export des inscriptions totales ou par date de disponibilit&eacute; des b&eacute;n&eacute;voles.</li>\r\n</ul>', 250, '2015-03-18', '2015-05-12', '2015-05-12', '2015-07-17', 1, 0 ],
                    [ 369, 13, 6, 'Top Garage', '<p>Pr&eacute;sentation de l\'application :</p>\r\n<ul>\r\n<li>Module de connexion &amp; r&eacute;cup&eacute;ration de mot de passe perdu.</li>\r\n<li>Profil "Admin" : Permet de g&eacute;rer (Ajout, modif et suppression) de tout.</li>\r\n<li>Profil "RDR" : Permet de g&eacute;rer ses coordinateurs, distributeurs, collaborateurs et garages associ&eacute;s.</li>\r\n<li>Profil "Coordinateur" : Permet de g&eacute;rer ses distributeurs, collaborateurs et garages associ&eacute;s.</li>\r\n<li>Profil "Distributeur" : Permet de g&eacute;rer ses collaborateurs et garages associ&eacute;s.</li>\r\n<li>Profil "Collaborateur" : Permet de g&eacute;rer ses garages associ&eacute;s.</li>\r\n<li>Profil "Garage" : Permet de g&eacute;rer ses informations personnelles. Il acc&egrave;de aux documents laiss&eacute;s par les admins, pour la cat&eacute;gorie &agrave; laquelle il est rattach&eacute;. Il g&egrave;re aussi son inscription en d&eacute;finissant les participants &agrave; l\'&eacute;v&eacute;nement.</li>\r\n<li>Profil "Fournisseur" : Permet de g&eacute;rer ses informations personnelles. Il acc&egrave;de aux documents laiss&eacute;s par les admins, pour la cat&eacute;gorie &agrave; laquelle il est rattach&eacute;. Il g&egrave;re aussi son inscription en d&eacute;finissant les participants &agrave; l\'&eacute;v&eacute;nement.</li>\r\n<li>Gestion de documents. Les admins mettent &agrave; disposition des documents (pdf, word, excel, images) en fonction des profils de chacun.</li>\r\n<li>Toutes modifications entrainera l\'envoi de mails informant les acteurs concern&eacute;s des changements (modification de profil, ajout de documents, ...).</li>\r\n</ul>\r\n<p><br />Le back office permettra:</p>\r\n<ul>\r\n<li>L\'affichage de tous les participants (garages + fournisseurs). Un code couleur permet rapidement de savoir qui participe ou non &agrave; l\'&eacute;v&eacute;nement.</li>\r\n<li>Une limitation dans l\'affichage des profils.</li>\r\n<li>Un tri sur les colonnes.</li>\r\n<li>Un export des donn&eacute;es en fonction des limitation et des tris.</li>\r\n</ul>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p><span style="text-decoration: underline;"><strong>DETAILS :</strong></span><br />&nbsp;&nbsp; &nbsp;Module de connexion &amp; r&eacute;cup&eacute;ration de mot de passe perdu.<br />100<br /><br />&nbsp;&nbsp; &nbsp;Profil "Admin" : Permet de g&eacute;rer (Ajout, modif et suppression) de tout.<br />&nbsp;&nbsp; &nbsp;Profil "RDR" : Permet de g&eacute;rer ses coordinateurs, distributeurs, collaborateurs et garages associ&eacute;s.<br />&nbsp;&nbsp; &nbsp;Profil "Coordinateur" : Permet de g&eacute;rer ses distributeurs, collaborateurs et garages associ&eacute;s.<br />&nbsp;&nbsp; &nbsp;Profil "Distributeur" : Permet de g&eacute;rer ses collaborateurs et garages associ&eacute;s.<br />&nbsp;&nbsp; &nbsp;Profil "Collaborateur" : Permet de g&eacute;rer ses garages associ&eacute;s.<br />&nbsp;&nbsp; &nbsp;Profil "Garage" : Permet de g&eacute;rer ses informations personnelles. Il acc&egrave;de aux documents laiss&eacute;s par les admins, pour la cat&eacute;gorie &agrave; laquelle il est rattach&eacute;. Il g&egrave;re aussi son inscription en d&eacute;finissant les participants &agrave; l\'&eacute;v&eacute;nement.<br />&nbsp;&nbsp; &nbsp;Profil "Fournisseur" : Permet de g&eacute;rer ses informations personnelles. Il acc&egrave;de aux documents laiss&eacute;s par les admins, pour la cat&eacute;gorie &agrave; laquelle il est rattach&eacute;. Il g&egrave;re aussi son inscription en d&eacute;finissant les participants &agrave; l\'&eacute;v&eacute;nement.<br />150 + 6x75 = 600<br /><br />&nbsp;&nbsp; &nbsp;Gestion de documents. Les admins mettent &agrave; disposition des documents (pdf, word, excel, images) en fonction des profils de chacun.<br />150<br /><br />&nbsp;&nbsp; &nbsp;Toutes modifications entrainera l\'envoi de mails informant les acteurs concern&eacute;s des changements (modification de profil, ajout de documents, ...).<br />100<br /><br /><br />&nbsp;&nbsp; &nbsp;L\'affichage de tous les participants (garages + fournisseurs). Un code couleur permet rapidement de savoir qui participe ou non &agrave; l\'&eacute;v&eacute;nement.<br />50<br /><br />&nbsp;&nbsp; &nbsp;Une limitation dans l\'affichage des profils.<br />50<br /><br />&nbsp;&nbsp; &nbsp;Un tri sur les colonnes.<br />-<br /><br />&nbsp;&nbsp; &nbsp;Un export des donn&eacute;es en fonction des limitation et des tris.<br />100<br /><br /></p>\r\n<p><strong>TOTAL : 1150&euro;</strong></p>\r\n<p><strong>Tarif propos&eacute; : 1290&euro;</strong></p>', 1900, '2015-03-24', '2015-06-01', '2015-06-01', '2015-06-09', 1, 0 ],
                    [ 370, 61, 6, 'Création de fanion', '<p>Cr&eacute;ation d\'un fanion &agrave; partir de Pos&eacute;idon.</p>\r\n<p>Version imprimable en 300dpi.</p>\r\n<p>&nbsp;</p>\r\n<p><strong>Julien : 50&euro;.</strong></p>', 100, '2015-03-27', '2015-04-22', '2015-04-22', '2015-06-05', 1, 0 ],
