                    [ 376, 48, 6, 'Hébergement et noms de domaine.', '<p>H&eacute;bergement / nom de domaine .com pour 1 an<br />Nom de domaine locationmanoir.fr pour 1 an</p>', 80, '2015-09-14', '2015-09-14', '2015-09-14', '2015-09-24', 1, 0 ],
                    [ 377, 30, 8, 'Gestion de documents', '<p><strong>Option 1 :</strong><br />- Cr&eacute;ation de comptes client prot&eacute;g&eacute;s par login / mot de passe.<br />- L\'admin ajoute des fichiers par glisser / d&eacute;poser ou en choisissant le fichier sur le PC.<br />- L\'ajout se fait dans un r&eacute;pertoire UNIQUE. Tous les types de fichiers de tous les clients sont m&eacute;lang&eacute;s.<br />- C&ocirc;t&eacute; client, on acc&egrave;de aux fichiers par les login / mot de passe d&eacute;finis par l\'admin.<br />- Le t&eacute;l&eacute;chargement des fichiers se fait au travers l\'URLs sp&eacute;cifiques qui emp&egrave;chent de pouvoir r&eacute;cup&eacute;rer n\'importe quels fichiers.<br /><strong>TARIF : 550&euro; --&gt; 590&euro;</strong><br /><br /><strong>Option 2 :</strong> <br />- Cr&eacute;ation de comptes client prot&eacute;g&eacute;s par login / mot de passe. <br />- La cr&eacute;ation d\'un nouveau client cr&eacute;e une arborescence sp&eacute;cifique de r&eacute;pertoires dans lesquels l\'admin pourra uploader des fichiers.<br />- L\'admin ajoute des fichiers par glisser / d&eacute;poser ou en choisissant le fichier sur le PC.<br />- C&ocirc;t&eacute; client, on acc&egrave;de aux fichiers par les login / mot de passe d&eacute;finis par l\'admin.<br />- Le client peut naviguer librement dans l\'arborescence mise &agrave; sa disposition.<br />- Le t&eacute;l&eacute;chargement des fichiers se fait au travers l\'URLs sp&eacute;cifiques qui emp&egrave;chent de pouvoir r&eacute;cup&eacute;rer n\'importe quels fichiers.<br /><strong>TARIF : 750&euro; --&gt; 790&euro;</strong><br /><br /><strong>Option 3 :</strong> <br />- Cr&eacute;ation de comptes client prot&eacute;g&eacute;s par login / mot de passe.<br />- L\'admin peut cr&eacute;er / modifier / supprimer les r&eacute;pertoires qu\'il souhaite pour ce client.<br />- Il s&eacute;lectionne un r&eacute;pertoire pis ajoute les fichiers par glisser / d&eacute;poser ou en choisissant le fichier sur le PC.<br />- C&ocirc;t&eacute; client, on acc&egrave;de aux fichiers par les login / mot de passe d&eacute;finis par l\'admin.<br />- Le client peut naviguer librement dans l\'arborescence mise &agrave; sa disposition.<br />- Le t&eacute;l&eacute;chargement des fichiers se fait au travers l\'URLs sp&eacute;cifiques qui emp&egrave;chent de pouvoir r&eacute;cup&eacute;rer n\'importe quels fichiers.<br /><strong>TARIF : 850&euro; --&gt; 890&euro;</strong></p>', 590, '2015-09-21', NULL, NULL, NULL, 0, 0 ],
                    [ 378, 30, 6, 'Modification du site lessecretsdelouise.com', '<p>Rendre les 5 rubriques du fronts administrables : (changer l\'image, le titre et le soustitre) <br />Veillez &eacute;galement &agrave; ce que l\'affichage soit correct dans la liste des produits en particulier, dans le fil d\'Ariane et autres.<br /><br />Ajouter une r&egrave;gle sur les frais de ports afin qu\'ils soient gratuits &agrave; partir d\'un certain param&egrave;tre fixe et en dur <br />(veillez &agrave; ce que ce parametre soit stock&eacute; dans l\'historique des commande)<br /><br />G&eacute;rer le stock en fonction des r&eacute;ponses Paypal mais surtout du traitement de la commande.<br /><br />Pouvoir modifier l\'ordre d\'affichage sur les cat&eacute;gories.<br /><br />Configurer le nouveau paiement Paypal quand il sera dispo.<br /><br /></p>', 590, '2015-09-21', '2015-11-07', '2015-11-07', '2015-11-13', 1, 0 ],
